import { reactive } from "vue";
export default {
  __name: 'AboutView',

  setup(__props, {
    expose
  }) {
    expose();
    let counter = reactive({
      value: 0
    });

    const increase_counter = () => {
      counter.value++;
    };

    const __returned__ = {
      counter,
      increase_counter,
      reactive
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

