import { defineStore } from "pinia";
export const useCounterStore = defineStore({
  id: "counter",
  state: () => ({
    counter: 0,
  }),
  getters: {
    doubleCount: (state) => state.counter,
  },
  actions: {
    increase() {
      this.counter++;
    },
    decrease() {
      this.counter--;
    },
  },
});
