import { computed } from "vue";
import { useCounterStore } from "../store/counter";
export default {
  __name: 'AboutView',

  setup(__props, {
    expose
  }) {
    expose();
    const store = useCounterStore(); // const counter = ref(0);
    // const store_value = ref(store.counter);

    const store_value = computed(() => {
      return store.counter;
    });

    const increase_counter = () => {
      store.increase();
    };

    const decrease_counter = () => {
      store.decrease();
    };

    const check = computed(() => {
      if (store.counter % 2 == 0) {
        return "even";
      } else {
        return "odd";
      }
    });
    const __returned__ = {
      store,
      store_value,
      increase_counter,
      decrease_counter,
      check,
      computed,
      useCounterStore
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
